const express = require('express');
const bodyParser = require('body-parser');
const fs = require('fs');
const path = require('path');
const axios = require('axios');
const { Octokit } = require('@octokit/rest');
require('dotenv').config();

const app = express();
app.use(bodyParser.json());
app.use(express.static('public'));

const REGISTRY_PATH = path.join(__dirname, 'metadata', 'prompts-registry.json');
const PROMPTS_DIR = path.join(__dirname, 'prompts');
const GENERATED_DIR = path.join(__dirname, 'generated');
const AGENT_TEMPLATE = path.join(__dirname, '.github', 'agents', 'prompt-selector.agent.md');

const octokit = process.env.GITHUB_TOKEN ? new Octokit({ auth: process.env.GITHUB_TOKEN }) : null;

function readRegistry() {
  if (!fs.existsSync(REGISTRY_PATH)) return { prompts: [], projectTypes: [] };
  return JSON.parse(fs.readFileSync(REGISTRY_PATH, 'utf8'));
}

// Endpoint: list prompts/collections
app.get('/api/prompts', (req, res) => {
  const registry = readRegistry();
  res.json(registry);
});

// Endpoint: recommend (simple) - filter by projectType or tag
app.get('/api/recommend', (req, res) => {
  const { projectType, phase, tag } = req.query;
  const registry = readRegistry();
  let p = registry.prompts || [];
  if (projectType) p = p.filter(x => (x.projectTypes || []).includes(projectType) || (x.projectTypes || []).includes('all'));
  if (phase) p = p.filter(x => (x.phase || []).includes(phase));
  if (tag) p = p.filter(x => (x.tags || []).includes(tag));
  res.json(p.slice(0, 50));
});

// Helper: ensure dir
function ensureDir(dir) {
  fs.mkdirSync(dir, { recursive: true });
}

// Helper: fetch remote file
async function fetchToFile(url, dest) {
  const r = await axios.get(url, { responseType: 'text' });
  fs.writeFileSync(dest, r.data, 'utf8');
}

// POST: create-project
// body: { name: string, projectType: string, prompts: [ids], createRepo: bool }
app.post('/api/create-project', async (req, res) => {
  try {
    const { name, projectType, prompts: chosenIds = [], createRepo } = req.body;
    if (!name) return res.status(400).json({ error: 'Missing project name' });

    const projectDir = path.join(GENERATED_DIR, name);
    if (fs.existsSync(projectDir)) return res.status(400).json({ error: 'Project already exists' });

    ensureDir(projectDir);
    ensureDir(path.join(projectDir, '.github', 'agents'));

    // Basic README
    fs.writeFileSync(path.join(projectDir, 'README.md'), `# ${name}\n\nGenerated by prompts-starter\n`, 'utf8');

    // Copy selected prompts to project/prompts
    ensureDir(path.join(projectDir, 'prompts'));
    const registry = readRegistry();
    for (const pid of chosenIds) {
      const meta = (registry.prompts || []).find(p => p.id === pid);
      if (!meta) continue;
      const dest = path.join(projectDir, 'prompts', path.basename(meta.path));
      if (meta.source && meta.source.startsWith('http')) {
        // fetch remote
        await fetchToFile(meta.source, dest);
      } else if (meta.path && fs.existsSync(path.join(__dirname, meta.path))) {
        fs.copyFileSync(path.join(__dirname, meta.path), dest);
      } else {
        // If path seems absolute or missing, try to fetch from source url
        if (meta.source && meta.source.startsWith('http')) {
          await fetchToFile(meta.source, dest);
        } else {
          // write a placeholder
          fs.writeFileSync(dest, `# Prompt ${meta.name}\n\nSource: ${meta.source || 'local?'}\n`, 'utf8');
        }
      }
    }

    // Copy agent template if exists
    if (fs.existsSync(AGENT_TEMPLATE)) {
      fs.copyFileSync(AGENT_TEMPLATE, path.join(projectDir, '.github', 'agents', 'prompt-selector.agent.md'));
    } else {
      // create a minimal agent file
      fs.writeFileSync(path.join(projectDir, '.github', 'agents', 'prompt-selector.agent.md'),
        `---\nname: "Prompt Selector"\ndescription: "Agent local per recomanar prompts"\nagent: agent\n---\n\n# Prompt Selector agent (placeholder)\n`, 'utf8');
    }

    // Create .gitignore
    fs.writeFileSync(path.join(projectDir, '.gitignore'), 'node_modules\n.env\n', 'utf8');

    // Initialize git locally and create initial commit (local only)
    const { execSync } = require('child_process');
    const cwd = projectDir;
    execSync('git init -b main', { cwd });
    execSync('git add .', { cwd });
    execSync('git commit -m "Initial scaffold created by prompts-starter"', { cwd });

    let repoUrl = null;
    if (createRepo && octokit) {
      // create repo in owner's account
      const owner = process.env.GITHUB_OWNER;
      const repoName = name;
      let created;
      if (owner) {
        // create in organization or user: user createForAuthenticatedUser, org createInOrg
        try {
          created = await octokit.repos.createInOrg({ org: owner, name: repoName, private: false });
        } catch (err) {
          // fallback to user
          created = await octokit.repos.createForAuthenticatedUser({ name: repoName, private: false });
        }
      } else {
        created = await octokit.repos.createForAuthenticatedUser({ name: repoName, private: false });
      }
      repoUrl = created.data.clone_url;
      // Add remote and push (will use user's local git credentials)
      // We won't embed token in remote to avoid leaking; instead return instructions to push.
    }

    // Response: project created; if repo created, return repoUrl and push instructions
    const instructions = [];
    if (repoUrl) {
      instructions.push(`Repository created at ${repoUrl}`);
      instructions.push('To push local commits to GitHub, run:');
      instructions.push(`  cd generated/${name}`);
      instructions.push(`  git remote add origin ${repoUrl}`);
      instructions.push('  git branch -M main');
      instructions.push('  git push -u origin main');
    } else {
      instructions.push('Project created locally at: generated/' + name);
      instructions.push('To create a repo and push, either:');
      instructions.push('  - Create a repo on GitHub and add it as remote; OR');
      instructions.push('  - Set GITHUB_TOKEN and restart server with createRepo=true to have the server create the repo for you (the server will still print push instructions).');
    }

    res.json({ ok: true, projectDir: `generated/${name}`, repoUrl, instructions });
  } catch (err) {
    console.error(err);
    res.status(500).json({ error: err.message || String(err) });
  }
});

const PORT = process.env.PORT || 3000;
ensureDir(GENERATED_DIR);
app.listen(PORT, () => console.log(`Server running on http://localhost:${PORT}`));
