```name=scripts/update-registry.js
// Simple registry builder: busca fitxers .prompt.md i extreu front-matter YAML (si existeix)
const fs = require('fs');
const path = require('path');
const yaml = require('js-yaml');

function scan(dir, base = '') {
  let results = [];
  if (!fs.existsSync(dir)) return results;
  const files = fs.readdirSync(dir);
  files.forEach(f => {
    const full = path.join(dir, f);
    const stat = fs.statSync(full);
    if (stat.isDirectory()) {
      results = results.concat(scan(full, path.join(base, f)));
    } else if (f.endsWith('.prompt.md')) {
      const content = fs.readFileSync(full, 'utf8');
      let meta = {};
      const match = content.match(/^---\n([\s\S]*?)\n---/);
      if (match) {
        try { meta = yaml.load(match[1]) || {}; } catch (e) {}
      }
      results.push({
        id: path.basename(f, '.prompt.md'),
        name: meta.name || path.basename(f, '.prompt.md'),
        path: path.join('prompts', base, f).replace(/\\/g,'/'),
        tags: meta.tags || [],
        projectTypes: meta.projectTypes || meta.projects || [],
        phase: meta.phase || [],
        description: meta.description || '',
        source: meta.source || null
      });
    }
  });
  return results;
}

const out = { prompts: scan(path.join(__dirname, '..', 'prompts')), projectTypes: [] };
fs.mkdirSync(path.join(__dirname, '..', 'metadata'), { recursive: true });
fs.writeFileSync(path.join(__dirname, '..', 'metadata', 'prompts-registry.json'), JSON.stringify(out, null, 2));
console.log('Registry updated:', out.prompts.length, 'prompts');
