    if (!name) return res.status(400).json({ error: 'Missing project name' });

    const projectDir = path.join(GENERATED_DIR, name);
    if (fs.existsSync(projectDir)) return res.status(400).json({ error: 'Project already exists' });

    ensureDir(projectDir);
    ensureDir(path.join(projectDir, '.github', 'agents'));
    ensureDir(path.join(projectDir, 'prompts'));

    fs.writeFileSync(path.join(projectDir, 'README.md'), `# ${name}\n\nGenerated by prompts-starter\n`, 'utf8');

    const registry = readRegistry();
    for (const pid of chosenIds) {
