    execSync('git init -b main', { cwd });
    execSync('git add .', { cwd });
    execSync('git commit -m "Initial scaffold created by prompts-starter"', { cwd });

    let repoUrl = null;
    const instructions = [];

    if (createRepo && octokit) {
