      instructions.push(`Repository created at ${repoUrl}`);
      instructions.push(`To push local commits:\n  cd generated/${name}\n  git remote add origin ${repoUrl}\n  git branch -M main\n  git push -u origin main`);
    } else {
      instructions.push('Project created locally at: generated/' + name);
      instructions.push('To create a GitHub repo: create it manually and add as remote, or set GITHUB_TOKEN and use createRepo=true.');
    }

    res.json({ ok: true, projectDir: `generated/${name}`, repoUrl, instructions });
  } catch (err) {
    console.error(err);
    res.status(500).json({ error: err.message || String(err) });
