import fs from "fs";
import path from "path";

const API_KEY = process.env.OPENAI_API_KEY;
if (!API_KEY) {
  console.error("ERROR: define OPENAI_API_KEY en las variables de entorno");
  process.exit(1);
}

const OUT_DIR = path.resolve(process.cwd(), "prompts");
if (!fs.existsSync(OUT_DIR)) fs.mkdirSync(OUT_DIR);

// Lista de temas o entradas que quieres convertir en prompts (puedes leer desde un archivo)
const temas = [
  "Crear prompt para generar tests unitarios en JavaScript para una funci√≥n que valida emails",
  "Prompt para que la IA genere README detallado para microservicio en Node.js con Docker",
  "Prompt para mejorar rendimiento SQL de una query con muchos JOINs",
];

// Funci√≥n que llama a la API de OpenAI (Chat Completions)
async function generarPrompt(tema) {
  const system = "Eres un asistente que genera prompts claros y √∫tiles para desarrolladores.";
  const user = `Dame 3 variantes de prompt concisas y muy √∫tiles para: "${tema}". Cada variante debe ser independiente, con instrucciones claras, ejemplos de entrada y salida cuando aplique.`;

  const resp = await fetch("https://api.openai.com/v1/chat/completions", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      "Authorization": `Bearer ${API_KEY}`,
    },
    body: JSON.stringify({
      model: "gpt-4", // o "gpt-4o" / "gpt-3.5-turbo" seg√∫n tu acceso
      messages: [
        { role: "system", content: system },
        { role: "user", content: user },
      ],
      temperature: 0.7,
      max_tokens: 800,
    }),
  });

  if (!resp.ok) {
    const errText = await resp.text();
    throw new Error(`OpenAI API error: ${resp.status} ${errText}`);
  }

  const data = await resp.json();
  const text = data.choices?.[0]?.message?.content ?? "";
  return text;
}

(async () => {
  for (const tema of temas) {
    try {
      console.log("Generando prompts para:", tema);
      const resultado = await generarPrompt(tema);

      // Nombre de archivo seguro
      const safeName = tema.replace(/[^a-z0-9-_]/gi, "_").slice(0, 60);
      const filename = path.join(OUT_DIR, `${safeName}.md`);

      const content = `# Prompts generados para: ${tema}\n\n${resultado}\n`;
      fs.writeFileSync(filename, content, "utf8");
      console.log("Guardado:", filename);
      // Espera peque√±a entre solicitudes para evitar l√≠mites (opcional)
      await new Promise((r) => setTimeout(r, 600));
    } catch (err) {
      console.error("Error generando para", tema, err.message || err);
    }
  }
  console.log("Proceso terminado. Revisa la carpeta ./prompts");
})();
